function rwwh(id,flag)
	if id == nil or id < 0 then
		return
	end
	if flag == nil or flag < 0 or flag > 2 then
		flag = 1
	end	
	if flag == 1 then
		return JY.Person[id]['外号']
	else
		return JY.Person[id]['外号2']
	end
end	
