newkdef[10117]=function()
say("是/否加入青城派。入门条件 :　实战 >= 60 拳 >= 100，剑 >= 100 ", 211, 0,"青城弟子")  --对话
	if MPPD(0) == 0 and JY.Person[0]["实战"] >= 60 and TrueQZ(0) >= 100 and TrueYJ(0) >= 100 then
		if DrawStrBoxYesNo(-1, -1, "是否要加入？", C_WHITE, CC.DefaultFont) then	
			JoinMP(0, 28, 1)
			MPAttrib(0)
			QZXS(CC.MPINFO2[28])
			say("恭喜你加入青城派。 ",211, 0,"青城弟子");
		else
			say("你可要好生考虑。 ",211, 0,"青城弟子");
		end	
	elseif MPPD(0) == 28 and MPDJ(0) >=1 then 
		if instruct_18(102) then  --判断是否有九阴白骨爪
			say("厉害，九阴白骨爪都拿到了。 ",211, 0,"青城弟子");
			say("这本摧心掌你拿着。 ",211, 0,"青城弟子");
			say("这不是九阴真经的武学吗？",0, 1);
			say("经过本门出尘子真人的推成出新，这套掌法有了新的变化。 ",211, 0,"青城弟子");
			say("原来如此，多谢！",0, 1);
			instruct_2(485,1)   --摧心掌
			if MPDJ(0) < 2 then
				JoinMP(0,28,2)
			end
		end
		if instruct_18(78) then  --判断是否辟邪剑法
            say("呵呵，原来这就是辟邪剑法，可笑可笑。 ",211, 0,"青城弟子");
			say("这位师兄...您好像看不起这辟邪剑法？",0, 1);
			say("并无看不起，只是在我看来和本门的松风剑法互相伯仲。",211, 0,"青城弟子");
			say("但就连咱们于观主都对此剑法评价颇高。 ",0, 1);
			say("呵呵，余沧海连自家剑法的门槛都没摸到，真是不值一提。 ",211, 0,"青城弟子");
			say("师兄，这样说于观主不好吧？ ",0, 1);
			say("看你平时对我挺好，过来我告诉你一个秘密。 ",211, 0,"青城弟子");
			say("这样.....这样.....这样.... ",211, 0,"青城弟子");
			say("原来如此，师兄我会替你保守秘密的。 ",0, 1);
			say("我就来传授你本门剑术精要，看好了....... ",211, 0,"青城弟子");
			say("原来是这样，多谢师兄。 ",0, 1);
			say("我要回师祖那里了，再会！！！！",211, 0,"青城弟子");
			instruct_3(-2,-2,0,0,0,0,0,0,0,0,0,-2,-2)  --修改场景事件    
			instruct_2(523,1)   --通幽诀
			if MPDJ(0) < 3 then
				JoinMP(0,28,3)
			end
		end	
	else
		say("可惜，你不符资格",211, 0,"青城弟子");
	end
Cls()  --清屏
do return end
end

