if JY.Base["天书数量"] < 4 then
	say("在大地图按F1可以使用马车。",905,0,"小朋友");
	do return end
else
    say("你要挑战我的朋友吗？",905,0,"小朋友");
	local DTSL = 0	--大庸双龙

	if JY.Base["天书数量"] > 3 then
	  DTSL = DTSL +1
	 end
 
	local challenge ={"大唐双龙"}

	local menu = {}
	for i = 1, #challenge do
		menu[i] = {challenge[i],nil,0}
	end
	if DTSL == 1 then
		menu[1][3] = 1
	end	
	DrawStrBox(CC.ScreenW/2-CC.DefaultFont*3-25, CC.ScreenH/2-CC.DefaultFont*7, "请选择一场战斗", C_GOLD, CC.DefaultFont, LimeGreen)
	local nexty = CC.ScreenH/2-CC.DefaultFont*7 + CC.SingleLineHeight
	local r = ShowMenu(menu, #menu, 0, CC.ScreenW/2-CC.DefaultFont*2-20, nexty, 0, 0, 1, 1, CC.DefaultFont, C_ORANGE, C_WHITE)
    local w = 0

	if r == 1 then
		Cls()
		if WarMain(379, 1) == false then  --战斗开始
			instruct_13()  --场景变亮
			say("请再接再厉。",905,0,"小朋友");
			Cls()  --清屏
			do return end  --无条件结束事件
		end
		instruct_13()  --场景变亮
		say(JY.Person[0]["外号"].."好身手。",905,0,"小朋友");
		instruct_2(388,1) --天刀
		SetTF(0,9725,1)  --获得顾道长生天赋
		SetTF(0,9996,1)  --获得闪电惊鸿天赋
		Cls()  --清屏
		menu[1][3] = 0
		do return end  --无条件结束事件
	end
end
end



newkdef[4105]=function()
	--无酒不欢：六如觉醒
	say("我可以帮你觉醒你体内潜在的能力，看你是否有缘人。",596,0);
	say("现在时机还未到，获得二本天书之后再来吧。",596,0);
	if JY.Base["标准"] > 0 then
		say("看你初入江湖，我决定帮你一把。",596,0);
		
		say("选择一个你喜欢的天赋外功吧。",596,0);
		
		local k = JY.Wugong;
		local menu = {}
		
		local kftype = nil
		if JY.Base['标准'] == 6 then
			kftype = JYMsgBox("请选择", "请选择喜欢的天赋外功类型", {"拳法","指法","剑法","刀法","奇门","内功"}, 6, 596)	
		else
			kftype = JYMsgBox("请选择", "请选择喜欢的天赋外功类型", {"拳法","指法","剑法","刀法","奇门"}, 5, 596)	
		end	
		for i = 1,JY.WugongNum-1 do
			local kfname = k[i]["名称"]
			if string.len(kfname) == 8 then
				kfname = kfname.."  "
			elseif string.len(kfname) == 6 then
				kfname = kfname.."    "
			elseif string.len(kfname) == 4 then
				kfname = kfname.."      "
			end
			menu[i] = {kfname,nil,2}
			if k[i]["武功类型"] == kftype and k[i]["攻击力10"] <= 900 then
				menu[i][3] = 1
			end
		end
		local nexty = CC.ScreenH/2-CC.DefaultFont*4 + CC.SingleLineHeight
		local r = ShowMenu2(menu, #menu, 4, 5, CC.ScreenW/2-CC.DefaultFont*10-20, nexty, 0, 0, 1, 0, CC.DefaultFont, C_ORANGE, C_WHITE,"领悟天赋外功")
		
		if r > 0 then
			local R = JYMsgBox("请选择", "请谨慎选择领悟的天赋外功位置。", {"天赋外功一","天赋外功二"}, 2, 596)
			if R == 1 then
				if JY.Person[0]["天赋外功2"] ~= r then
					SetTianWai(0, 1, r)
				else
					DrawStrBoxWaitKey("天赋外功不可重复。",C_ORANGE, CC.DefaultFont)
					Cls()
					instruct_2(345,1)	
				end	
			elseif R == 2 then
				if JY.Person[0]["天赋外功1"] ~= r then
					SetTianWai(0, 2, r)
				else					
					DrawStrBoxWaitKey("天赋外功不可重复。",C_ORANGE, CC.DefaultFont)
					Cls()
					instruct_2(345,1)	
				end
			end
		end
	end
	addevent(-2, -2, -2, 4106, 1)
	do return end
end

