newkdef[4107]=function()
    if JY.Base["天书数量"] < 4 then
	    say("获得四本天书之后再来吧，目前你还无法善用这力量。",596,0);
		do return end
	else
	say("行走江湖天赋内功很重要的哦，战胜我爹爹他会指点你的内功修为，你准备好了么？",596,0);
	say("请指点……",0,1);
	if DrawStrBoxYesNo(-1, -1,"要挑战谢云流修炼天赋内功吗？", C_WHITE, CC.DefaultFont) then		
		say("我就陪你练练。",501,0);		
		if WarMain(305) then			
			light()			
			say("功夫不错！",501,0);			
			say("小村传人果然不错，回去多观察木人，会有新的发现。",501,0);
			PlayMIDI(3)	
		    SetJX(0,1)
			--JY.Person[0]["六如觉醒"] = 1
		    if MPPD(0) == 11 then
	            say("弟子见过宗主！",0,1)
			    say("无悠已经告诉我了，既加入我刀宗就莫要败坏刀宗名声。",501,0);
				say("弟子明白！",0,1)
				say("我再来指点你一些刀宗的高深武学。",501,0);
				say("多谢宗主！",0,1)
				JY.Person[0]["天赋内功"] = 152   
				instruct_2(311,1)                 --太虚剑意
		        instruct_35(0,1,152,1)            --主角第二格太虚剑意1级
				if MPDJ(0) < 2 then
					JoinMP(0, 11, 2)
				end
			end 	
		local k = JY.Wugong;
	    local menu = {}	
	    local kftype = JYMsgBox("请选择", "天赋内功可以无视阴阳内功*属性修炼，请选择喜欢的天*赋内功类型", {"内功",}, 1, 347)
	    for i = 1,JY.WugongNum-1 do
		    local kfname = k[i]["名称"]
		    if string.len(kfname) == 8 then
			    kfname = kfname.."  "
		    elseif string.len(kfname) == 6 then
			    kfname = kfname.."    "
		      elseif string.len(kfname) == 4 then
			      kfname = kfname.."      "
		    end
		        menu[i] = {kfname,nil,2}
		    if k[i]["武功类型"] == 6 and i ~= 354 then
			    menu[i][3] = 1
		    end
	    end
	    local nexty = CC.ScreenH/2-CC.DefaultFont*4 + CC.SingleLineHeight
	    local r = ShowMenu2(menu, #menu, 4, 5, CC.ScreenW/2-CC.DefaultFont*10-20, nexty, 0, 0, 1, 0, CC.DefaultFont, C_ORANGE, C_WHITE,"领悟天赋内功")	
	    if r > 0 then
		    SetTianNei(0, r)
		    for i = 1, JY.Base["武功数量"] do
			    local kfid = JY.Person[0]["武功"..i]
			    if kfid == r then
				    JY.Person[0]["武功等级"..i] = 999
				    break
			    end
		    end
		else
			do return end    
        end
	    if JY.Base["标准"] == 7 then
		    SetTF(0,9933,1)
		end
        if JY.Base["畅想"] == 596 or JY.Base["畅想"] == 501 then
          instruct_2(313,1)
          instruct_2(311,1)
          instruct_35(596,0,151,0)
          instruct_35(596,1,152,0)
          instruct_35(501,0,151,0)
          instruct_35(501,1,152,0)
        end	
		local xid = 457;
            if JY.Base["畅想"] == 457 then
                xid = 0
            end
        AddPersonAttrib(xid, "武学常识", 30);
        AddPersonAttrib(xid, "御剑能力", 120)
		say("这个你拿着，十本天书后拿着它到中华阁来找我与爹爹。",596,0);	
		instruct_2(312, 1)  --得到或失去物品
	    say("我期待将来成长后的你，小村传人。",596,0);		
	    dark()	
	    null(-2, -2)
	    light()
	    say("谢家父女真是强悍阿",0,1);
	    say("跟他们的对战让我叵有感悟!似乎该开创自己的路了！",0,1);
	    --JY.Person[0]["门派技能1"] = 1
	    Cat('融合武功',0,6)
	    QZXS("成功自创内功！")
		instruct_35(767,1,204,999)           --青龙斗气第2格擒龙功10级
		instruct_35(768,1,171,999)           --白虎斗气第2格太极神功10级
		instruct_35(769,1,105,999)           --朱雀斗气第2格葵花神功10级
		instruct_35(770,1,108,999)           --玄武斗气第2格易筋经10级
		instruct_35(776,0,109,999)           --齐木思第1格野球拳
		instruct_35(776,1,110,999)           --齐木思第2格神山剑法
		--极限挑战
		if JY.Person[456]["论剑奖励"] == 1 then
			JY.Person[456]["论剑奖励"] = 2 
		end
	    do return end
    else
		instruct_15()
		do return end	
	  end
	end
	end
end

