newkdef[599]=function()
if JY.Base["畅想"] == 76 and instruct_16(49) == false and MPPD(0) == 0 then --畅想王语嫣无条件入逍遥
	say('聪辩先生当面，小女子王语嫣向您问好',0,1)
	say('当世已无聪辩先生，只剩一个摆下棋局的聋哑老人罢了，小女娃知晓老朽过往名号，不知哪家故人之后？',115,0)
	say('Ｄ＜摇了摇头＞Ｂ却还恕小女子不能详实已告了，况且小女子今日上山来此，另有要事，便是为破这\"珍珑\"而来',0,1)
	say('你年纪轻轻，竟知这棋局之名？你可知这棋局已经让多少自负棋艺高绝之人深现其中，心神狂乱吗？老朽虽广邀天下青年俊杰前来破解此局，却也要劝小女娃一句，莫要妄言阿！',115,0)
	say('Ｄ＜心里暗暗说道＞Ｂ狂妄之极！老朽苦心钻研此局三十馀年，尚不得其解。如今你一个小小女娃竟敢大放厥词？',115,0)
	say('Ｄ＜轻轻一笑，重重的说了前几个字＞Ｂ「聋哑老先生」还请息怒，小女子绝无看轻此棋局和先生之意，只是对\"珍珑\"有些奇思妙想，不如就让小女子一试？',0,1)
	say('那么，就请下子吧。',115,0)
	QZXS('王语嫣款款坐下，拈起一颗白子，毫不犹豫的下在棋盘之上')
	Cls()  --清屏
	say("胡闹，胡闹，你自填一气，自己杀死一块白棋，哪有这等的下法。", 115, 0)  --对话
	Cls()  --清屏
	say("还请老先生再斟酌一番，小女子此手，当真是一步臭棋吗？", 0, 1)  --对话
	Cls()  --清屏
	say("Ｄ＜略一沉吟＞Ｂ这……这……这\"珍珑\"竟然解了，原来关键在于第一着的怪棋。这局棋原本纠缠于得失胜败之中，以至无可破解，姑娘这一着不着意于生死，更不着意于胜败，反而勘破了生死，得到解脱…………", 115, 0)  --对话
	Cls()  --清屏
	say("小女子也是过往有幸看过\"珍珑\"残局，研究多日后一时气愤，胡乱一下才发现这步怪棋。", 0,1)  --对话
	Cls()  --清屏
	say("Ｄ＜虚竹先生，还请原谅我……＞Ｂ", 0,1)  --对话
	Cls()  --清屏
	say("姑娘无须如此谦虚，能解这\"珍珑\"之人，不是天纵英才便是福缘深厚之辈，老朽却认为姑娘是二者兼具啊！可喜可贺，还请姑娘入屋内。", 115, 0)  --对话
	Cls()  --清屏
