	stands()
	Cls()  --清屏
	instruct_13()  --场景变亮
	--instruct_25(17,28,24,19)  --场景移动
	say('星河怎么便放了个小女娃进来？罢了，能解\"珍珑\"便是证明，且小女娃也生的俊俏，为我传人可也。跪下磕头吧！',116,0)
	say('Ｄ＜怎么觉得这小女娃看着如此眼熟？＞Ｂ',116,0)
	say("Ｄ＜缓缓跪倒＞Ｂ外公，语嫣来看您了", 0,1)  --对话
	say("Ｄ＜眼神呆楞，宛若雷击＞Ｂ，你喊我什么？", 116,0)  --对话
	say("外公，我名叫王语嫣，家母名讳李青萝。", 0,1)  --对话
	say("青，青萝……Ｄ＜老泪纵横＞Ｂ没想到老夫竟还能有听到这名字，和见到青萝后人的一天……", 116,0)  --对话
	say("你，你娘，还好么？你姓王，想来她应当嫁了人，你父亲，对青萝可好？你又过的好么？", 116,0)  --对话
	say('我娘和我，还有我的「两个」父亲……',0,1)
	dark()
	light()
	say('青萝这性子，还真的是跟她母亲相似，但却也怪我呀……',116,0)
	say('我在年轻时，辜负了你外祖母李秋水。多年来我虽对秋水感到抱歉，却并不后悔。',116,0)
	say('而我原本也以为我对我跟秋水的孩子，也就是你娘可以不思不念无牵无挂，但随着日子过去，我的脑子里除了报仇之外，却是越来越念着青萝孩儿，更是无比后悔当年我干的混帐事……',116,0)
	say('Ｄ＜无比哽咽，语音沙哑＞Ｂ我怎么可以就放着青萝不管啊……',116,0)
	say('还被那段小子给如此始乱终弃……我真该死阿……',116,0)
	say("Ｄ＜默默无语＞Ｂ", 0,1)  --对话
	say('Ｄ＜过了好一会儿，终于收泪＞Ｂ事已至此，能见到你，知道你娘安好，我便算是了却了一桩心事了。',116,0)
	say("外公不想去看看娘亲么？", 0,1)  --对话
	say('怎么不想？但我……没这个颜面，况且……也去不成了。你能解开珍珑，天资聪慧，又生的一副好面孔，本就是我逍遥派的好苗子。我又得知你与你娘皆安好的消息',116,0)
	say('这解了我心中不少执念，我可以感觉到，我即将走到尽头了。',116,0)
	say('来吧孩子，我这一身功力当不可随我入土，外公这便将吾一身七十馀年的内力予你，想来当能保你平安！',116,0)
	dark()
	light()
	QZXS('两人缓缓收功，王语嫣浑身气机冲盈，显然武功大进')
