newkdef[1121]=function()
	if instruct_31(10000) == false then  --判断银两数量
		say("客官您里边请！", 511, 2) --对话
		return
	end
	say('客官您里边请～*嘿！客官，一看您就是江湖人士，我这有上等货色，只要1W两，不知客官有没有兴趣？',511,2)
	say('有好东西？是什么？让我瞧瞧再说…….咦？武功秘籍？这是…….',0,1)
	say('客官好眼力，这是《葵花点穴手》，我的独门绝学。',511,2)
	say('…….一本破秘籍就要3W两，你怎么不去抢？',0,1)
	say('客官这是哪儿的话，抢是要担风险的。',511,2)
	if DrawStrBoxYesNo(-1, -1, "是否要花1W两买秘籍？", C_WHITE, CC.DefaultFont) then  --是/否
		say("Ｄ（终于卖出去了）Ｂ哎呀，客官绝对会觉得物有所值的。", 511, 2) --对话
		Cls()  --清屏
		instruct_2(370,1)
		instruct_2(174,-10000)
		addevent(-2,2,1,1122,1)
	else 
		say("哎呀，客官已经错失良机了。", 511, 2) --对话
		Cls()  --清屏
		dark()
		null(-2,2)
		light()
	end
end

