newkdef[1123]=function()
    if DrawStrBoxYesNo(-1, -1, "外族高人，要挑战吗？", C_ORANGE, CC.DefaultFont) then
        if WarMain(356) then
            CC.TX["天关"] = 4
			local menu1 = {}
			local TFtable = {}
			for i = 1,#CC.Tiangtf do
				menu1[#menu1+1] = i
			end
			RandFetch(TFtable,1,#menu1)
			local r1 = CC.Tiangtf[TFtable[1]]
			local menu = {}
			if r1 ~= nil then
				menu[1] = CC.PTFSM[r1][1]
			else
				menu[1] = "无天赋"	
			end 
			DrawStrBoxWaitKey("获得周目天赋["..menu[1]..']', C_GOLD, CC.DefaultFont,nil, C_RED)
			Cls()
			say("不错不错，请选择奖励后请前往下一层挑战吧。",596,0);			
			CC.TGJL[#CC.TGJL+1] = r1
			Cls()
			dark()
			null(-2,1)
			light()
			--end
			removebyvalue(CC.Tiangtf,CC.Tiangtf[TFtable[1]], CC.Tiangtf[TFtable[1]])	
		end
	end
	do return end  --无条件结束事件
end

