newkdef[1128]=function()
  say("范帮主，田相公，张家兄弟从关外一路跟随这点子夫妻南来，查得确确实实，铁盒儿确是在点子身上。 Ｒ＜众人听到“铁盒儿”三字，相互望了一眼，都想：“说到正题啦。”＞",725,0)
  Cls()
