  Cls()
  say("Ｒ＜范帮主点了点头＞Ｂ你知道就好啦。",723,0)
  Cls()
  say("Ｒ＜脸一沉，夹手将张大哥的刀夺了去，折为两段，抛在地下＞Ｂ我不想多伤人命，快滚吧！",633,0)
  Cls()
	say("Ｒ＜见人多的一方一拥而上，张飞雄飞脚去踢胡一刀娘子的大肚子＞Ｂ小心！",633,0)
	instruct_30(25,59,25,51)
	instruct_30(25,51,28,51)
	instruct_30(28,51,28,47)
	instruct_30(28,47,26,47)
	stands()
  Cls()
  if WarMain(374) == false then
	   instruct_15() 
	  do return end
  end
  say("Ｒ＜大怒＞Ｂ我本欲相饶，你们竟如此无礼！Ｒ＜抢了一把刀，一口气伤了七人＞",633,0)
  Cls()
  say("大哥，算啦，给你没出世的孩子积积德吧！刚才多谢这位小哥援手",738,0)
	Cls()
	dark()
	for i = 25,30 do
	   null(-2,i)
	end
	light()
  say("胡某谢过小兄弟义举援手！",633,0)
  Cls()
  say("胡大侠客气了。",0,1)
  Cls()
  say("胡某还有要事在身，后会有期！",633,0)
  Cls()
  say("后会有期。＜见胡一刀架着马车向Ｒ北方有间客栈Ｂ方向行去。＞",0,1)
	dark()
	null(-2,8)
	null(-2,36)
	null(-2,33)
    null(-2,17)
    null(-2,24)
	addevent(-2,32,1,nil,nil,4838*2)
	addevent(-2,34,1,nil,nil,4825*2)
	light()
	instruct_37(1)
	do return end
end

newkdef[1129]=function()
	say("Ｄ＜先在这客栈住一晚……＞Ｂ",0,1)
	dark()
	light()
	instruct_19(39,12)
	instruct_40(3)
	stands()
	instruct_25(38,10,41,20) 
	Cls()	
	say("大哥，是谁来了？",738,0)
	Cls()
 	say("几个毛贼，你好好睡罢！别担心。",633,0)
	Cls()
	say("Ｄ＜低叹＞Ｂ不用骗我，是金面佛来啦。",738,0)
	Cls()
	say("不是的，你别瞎疑心。",633,0)
	Cls()
	say("那你干麽说话声音发抖？你从来不是这样的。",738,0)
	Cls()
	say("Ｄ＜胡一刀不语，隔了片刻＞Ｂ你猜到就算啦。我不会怕他的。",633,0)
	Cls()
	say("大哥，你千万别为了我，为了孩子担心。你心里一怕，就打他不过了",738,0)
	Cls()
	say("Ｄ＜叹了口长气＞Ｂ也不知道为什麽，我从来天不怕地不怕，今晚抱著孩子，见到金面佛进来，他把包袱往桌上一放，眼角向孩子一幌，我就全身出了一阵冷汗。妹子，你说得不错，我就是怕金面佛。",633,0)
	Cls()
	say("你不是自己怕他，是怕他害我，怕他害咱们的孩子。",738,0)
	Cls()
	say("听说金面佛行侠仗义，江湖上都叫他苗大侠，总不会害女人孩子吧？Ｄ＜他说这几句话时声音更加发颤，显是心里半分儿也拿不准＞Ｂ",633,0)
	Cls()
	say("大哥，你抱了孩子，回家去吧。等我养好身子，到关外寻你。",738,0)
	Cls()
	say("唉，那怎麽成？要死，咱俩也死在一块",633,0)
	Cls()
	say("早知如此，当年我不阻你南来跟金面佛挑战倒好。那时你心无牵挂，准能胜他。",738,0)
	Cls()
	say("Ｄ＜笑道＞Ｂ今日相逢，也未必就败在他手里。他那个“打遍天下无敌手”的黄包袱，只怕得换换主儿。Ｄ＜他虽然带笑而说，但声音总是发颤＞Ｂ",633,0)
	Cls()
	say("大哥，你答应我一件事。",738,0)
	Cls()
	say("什麽？",633,0)
	Cls()
	say("咱们把一切跟金面佛明说了，瞧他怎麽说。他号称大侠，难道不讲道理？",738,0)
	Cls()
	say("我在外面一边喝酒，一边心中琢磨，十几条可行的路子都细细想过了。你刚生下孩子，怎能出外？我自己去，一说就僵。倘若有个人能使，你的主意倒也行得。",633,0)
	Cls()
	say("那个医生倒挺能干的，口齿伶俐，不如烦他一行。",738,0)
	Cls()
	say("此人贪财，未必可靠。",633,0)
	Cls()
	say("咱们重重酬谢他就是。",738,0)
	Cls()
	dark()
	instruct_19(38,17)
	instruct_40(2)
	light()
	instruct_30(38,17,38,20)
	instruct_30(38,20,39,20)
	say("胡大侠！我们又见面了。",0,1)
	Cls()
	say("兄弟来得正好，与我喝个痛快。",633,0)
	Cls()
	say("喝酒不急，刚才胡大侠与嫂夫人说的话我都听到了，可有在下效劳之处？",0,1)
	Cls()
	say("兄弟竟然说了，那就麻烦你帮我找下隔壁厢房的医生，他识得“打遍天下无敌手”苗人凤，然后如些这般这般……。",633,0)
	Cls()
	say("我这就去办，＜那医生也在这Ｒ有间客栈Ｂ里。＞",0,1)
	Cls()
	null(-2,31)
	addevent(-2,37,0,1130,3,nil)
	addevent(-2,34,1,1131,1,nil)
	addevent(-2,32,1,1131,1,nil)
	do return end
end

