newkdef[1175]=function()
	if instruct_16(635)  and instruct_18(308) then
		say("一过十年，足下安然无恙，云某真有不胜之喜！",753,0)
		Cls()
		say("Ｄ＜淡淡道＞Ｂ尊驾尚在人间，梁某岂敢先亡？不过尊驾来得甚巧，再晚一分半分，怕就见不着我了。",635,0)
		Cls()
		say("突发战事，云某一时脱不得身，故而才请大伙儿前来陪你一阵。天幸今日赶的及时，倘若你死在他人剑下，云某岂非终身抱憾？",753,0)
		Cls()
		say("Ｄ＜微微一笑＞Ｂ闲话少说，你们一齐上来，还是车轮战法？",635,0)
		Cls()
		say("云某既然来了，群殴烂打、车轮战法当然统统不用。",753,0)
		Cls()
		say("那便是单打独斗了？",635,0)
		Cls()
		say("不错，十余年心愿，只愿今朝得偿。",753,0)
		Cls()
		say("Ｄ＜淡淡点头＞Ｂ还是花宫主爽快，今日既分胜负，又决生死了？",635,0)
		Cls()
		say("Ｄ＜凝色道＞Ｂ不错，十余年心愿，只愿今朝得偿。",753,0)
		Cls()
		say("Ｄ＜沉吟道＞Ｂ梁某倘若败了，万事俱休。倘若侥幸胜了，该当若何？",635,0)
		Cls()
		say("若你胜了，自然无人阻你离开！Ｄ＜云殊手按剑柄，拔出剑来，剑身光亮清澈，隐闪赤芒，云殊手拈剑锋，沉声道＞Ｂ此剑久经杀戮，刃间有血光涌动，宛若火光，故名炎龙。在云某手里，已斩三千三百九十四人，足下是三千三百九十五个。",749,0)
		Cls()
		say("Ｄ＜笑道＞Ｂ九五乃是至尊之数，不才若能授首，却也幸甚。但不知，那三千三百九十四人中，又有几个恶人，几个好人？",635,0)
		Cls()
		say("Ｄ＜面色微变，沉吟道＞Ｂ成大事者不拘小节，难免错杀无辜。",753,0)
		Cls()
		say("Ｄ＜点头道＞Ｂ这话足见坦荡。Ｄ＜说着拔出天罚剑来，众人瞧得是把锈剑，均是大笑。＞Ｂ草木为剑，也可伤人。何况此剑乃是天下第一剑，铸成以来，仅杀一人。",635,0)
		Cls()
		say("Ｄ＜神色微微一变，冷然道＞Ｂ天下第一剑？哼，不打诳语么？",753,0)
		Cls()
		say("决非诳语！",635,0)
		Cls()
		say("好，阁下请了！",753,0)
		Cls()						
		if WarMain(3) == false then
			instruct_15()
			Cls()
			do return end
		end
		say("Ｄ＜梁萧这招“周圆剑”并非杀着，否则剑锋直落，云殊早已双腕齐断，哪知剑意未绝，云殊竟束手待死，一时颇感意外，是以长剑停在半空，不知该否削下。霎时间，身后锐风忽起，若有兵刃刺来。梁萧趁机反手出剑，挑中那人剑身，那人倒退两步，俏脸苍白，但眸子秋水也似，清亮冰冷。不是别人，正是花慕容。＞Ｂ慕容，你做什么？",749,0)
		Cls()
		say("Ｄ＜容凄然一笑，道＞Ｂ做什么？难道什么也不做，瞧你就死么？",1064,0,"花慕容")
		Cls()
		say("我与他约定在先，单打独斗，生死由命，你这般做岂非叫我食言而肥？再说这男人的事情，你女人家不要多管。",753,0)
		Cls()
		say("女人？女人就不是人吗？女人就不知爱恨了吗？不错，什么复国大计、江湖道义，我都不懂。我只知道，我可以没有丈夫，但女儿不能没有父亲！",1064,0,"花慕容")
		Cls()
		say("Ｄ＜花慕容心念一转，仿佛过了十年光阴，蓦地银牙紧咬，展剑刺向梁萧。当此之时，梁萧进退两难，花慕容长剑既来，也唯有举剑抵挡。却听花无媸蓦地叫声＞Ｂ清渊。",749,0)
		Cls()	
		say("是。Ｄ＜“太阿剑”拔出鞘来，迎风一指，刺到梁萧面门，梁萧不大愿意和他交手，长剑下指，飘然后退＞Ｂ",755,0)
		Cls()
		--公羊羽出现
		dark()
		--addevent()
		light()
		say("Ｄ＜忍住悲戚＞Ｂ师父，你怎地来了？",753,0)
		Cls()
		say("Ｄ＜冷笑＞Ｂ我若是不来，你收拾得了么？Ｄ＜忽地动步，拂袖将花清渊带到一旁，叹道＞Ｂ这一阵让于我吧。",753,0)
		Cls()	
		say("Ｄ＜怒道＞Ｂ不要脸，说好单打独斗，现在又是二打一，又是车轮战……",899,0,"风伶")
		Cls()
		if WarMain(2) == false then
			instruct_15()
			do return end
		end
		DrawStrBoxWaitKey("梁萧领悟十方步", C_GOLD, CC.DefaultFont,nil, C_RED)
		instruct_35(635,1,379,999)
		JY.Person[635]["主运轻功"] = 379 
		do return end
	end
end	
