--换秘籍
newkdef[3008]=function()
	local function wupingisgongType(wuid,typeId)
		local ggid = JY.Thing[wuid]["练出武功"]
		if ggid < 0 then
			return false
		end
		return JY.Wugong[ggid]["武功类型"] == typeId
	end
	say(JY.Person[0]["外号2"].."，如果你能拿到一定数量的Ｒ秘籍残章Ｂ，可以来找我换取一些武功秘籍。",1063,1,"李青萝")
		--ceshi 物品表
		local k = JY.Thing;
		local ceshi = {}
	local gongtype = JYMsgBox("请选择秘籍类型", "    换取武功秘籍需要花费的残章数量是：   *     练出的武功品阶x20，*一经换取不退不换。", {"拳法","指法","剑法","刀法","奇门","内功","轻功","杂类"}, 8, 906)
	if gongtype < 0 then
		return
	end
	for i = 0, JY.ThingNum - 1 do
		if i < 390 or  i == 422  or i == 423 or i == 428 or i == 452 or i ==463 or i == 474 or i ==483 or i == 492 or i == 499 
		or i == 504 or i == 507 or i == 508 or i == 515 or i == 525 or i == 526 or i == 527 then
		local kfname = k[i]["名称"]
		if string.len(kfname) == 8 then
			kfname = kfname.."  "
		elseif string.len(kfname) == 6 then
			kfname = kfname.."    "
		elseif string.len(kfname) == 4 then
			kfname = kfname.."      "
		end
	
			ceshi[i] = {kfname,nil,2}
		if k[i]["类型"] == 2 then
			ceshi[i][3] = 1
			if wupingisgongType(i,gongtype)~=true then
				ceshi[i][3] = 2
			end
			if  JY.Thing[i]["练出武功"] < 0 and gongtype == 8 then
				ceshi[i][3] = 1
			end
		end	
	end	
	end
	local nexty = CC.ScreenH/2-CC.DefaultFont*4 + CC.SingleLineHeight
	local r = 0
	r = ShowMenu2(ceshi, #ceshi, 4, 9, CC.ScreenW/2-CC.DefaultFont*10-20, nexty, 0, 0, 1, 1, CC.DefaultFont, C_ORANGE, C_WHITE,"请选择")
	if r == 0 then
		return
	end
	local wg = JY.Thing[r]["练出武功"]
	if ( CC.MJCZ - JY.Wugong[JY.Thing[r]["练出武功"]]["层级"]*20) >= 0 then
		instruct_2(r,1)
		CC.MJCZ = CC.MJCZ - JY.Wugong[JY.Thing[r]["练出武功"]]["层级"]*20
		instruct_2(550,-JY.Wugong[JY.Thing[r]["练出武功"]]["层级"]*20)
	else
		say(JY.Person[0]["外号2"].."，秘籍残章数量不够哦。",1063,1,"李青萝")
	end
	return
end

