			say("哈哈哈还真给本门长脸了",514, 0,"杨冰羽");
			say("你和本门的名头想必震惊江湖了吧。",514, 0,"杨冰羽");
			say("宗主过誉了。",0, 1);
			say(JY.Person[0]["外号2"].."你别谦让了，本宗主说话很实在的",514, 0,"杨冰羽");
			say("寒冰真气先别练了，这本秘籍拿去吧！",514, 0,"杨冰羽");
			instruct_2(413,1)
			SetTF(0,9740,1)
			say("《易水寒诀》？没听过本门有这武功啊？",0, 1);
			say("昨天才创出来的你当然没听过",514, 0,"杨冰羽");
			say("你差点见不到本宗主啊哈哈哈！",514, 0,"杨冰羽");
			say("嗯？宗主此话怎讲？",0, 1);
			say("嘿，详情我就不细说了，总之就是本宗主碰到了个跟冰块一样的家伙",514, 0,"杨冰羽");
			say("那一身寒气和刀气，啧啧啧……",514, 0,"杨冰羽");
			say("本宗主跟那个姓聂的家伙打了一架，差点没死在他的《傲寒六诀》之下",514, 0,"杨冰羽");
			say("Ｌ＜聂？傲寒六诀……？＞Ｂ难道是！？",0,1);
			say("嘿嘿嘿，不愧是小村传人，消息灵通啊",514, 0,"杨冰羽");
			say("但差点就是差点，本宗主活下来了，还透过《傲寒六诀》改善了寒冰真气",514, 0,"杨冰羽");
			say("就是你现在看到的《易水寒诀》了，此功不敢说胜过傲寒六诀，但寒冰真气为他提鞋也不配！",514, 0,"杨冰羽");
			say("原来竟有这般曲折……多谢宗主！",0,1);
			say("哈，谢是不用，以后继续打拼本门的侠名就可以了，羽长！",514, 0,"杨冰羽");
			say("本宗主要远行游历去了，羽长，本门需要你多多打点了！",514, 0,"杨冰羽");
			instruct_2(91, 1)  --玄冥神掌
			JY.Person[514]["武功2"] = 43
			JY.Person[514]["武功等级2"] = 999
			if MPDJ(0) < 3 then
				JoinMP(0,20,3)
			end
			do return end
		else
			say("有没有好好练功啊！",514, 0,"杨冰羽");
		end
	end
	Cls()  --清屏
	do return end
end


