newkdef[3224] = function() -- 夜雨楼暗器生意
	--say('嗨嗨！你来啦！今天来找我有啥事海',899,0,'小雨')
	local r = JYMsgBox("小雨小间","嗨嗨！你来啦！今天来找我有啥事海",{"买暗器","忘手法"},2,899,1)
	if r == 1 then
		instruct_64()
	elseif r == 2 then
		say('想来遗忘暗器手法啊？那你可算是找对人了。',899,0,'小雨')
		say('所谓差之毫厘，谬以千里～要是乱学，这手法会很乱的。',899,0,'小雨')
		say('但本姑娘可是来自…呃，不重要，反正本姑娘有密传来调整你的手法。',899,0,'小雨')
		say('但是本姑娘最近为了研究，把手头钱都给烧光了…',899,0,'小雨')
		say('所，所以，这项服务，本姑娘得收点银子，呵呵。',899,0,'小雨')
		say('啊，那是理所当然，不知道小雨姑娘需要多少？',0,1)
		say('Ｄ<面色微红，比出了五>Ｂ五，五万两…',899,0,'小雨')
		say('Ｄ<乍舌>Ｂ五，五万两？',0,1)
		if DrawStrBoxYesNo(-1, -1, "是否要花费五万两？", C_WHITE, CC.DefaultFont) then
			if instruct_31(50000) then
				say('Ｄ<一脸肉痛，喃喃自语>Ｂ专业有价、尊重专业……',0,1)
				instruct_2(174,-50000)
				say('Ｄ<喜笑颜开>Ｂ哈哈承蒙惠顾～',899,0,'小雨')
				say('想要让谁遗忘暗器手法呢？',899,0,'小雨')
				local nexty = CC.MainSubMenuY + CC.SingleLineHeight
				local p = SelectTeamMenu(CC.MainSubMenuX, nexty)
				local diyid = JY.Base["队伍" .. p]
				if JY.Person[diyid]['门派技能2'] < 1 then
					say('咦？'..JY.Person[diyid]['姓名']..'根本就还没学过暗器啊？要遗忘个啥？',899,0,'小雨')
					say('呃，我记错了。能重选不？',0,1)
					say('当然可以，不过刚刚的5万两，我可不会还你喔～',899,0,'小雨')
					say('你，怎么可以这样？',0,1)
					say('没，没办法，我真的需要钱…。',899,0,'小雨')
					say('……Ｄ<无语问苍天>Ｂ',0,1)
					do return end
				end
				dark()
				JY.Person[diyid]['门派技能2'] = 0
				light()
				if JY.Person[diyid]['门派技能2'] == 0 then
					QZXS('暗器手法成功遗忘了！')
				end	
				say('成功啦！欢迎下次再来啊～',899,0,'小雨')
			else
				say('钱不够还骗我！哼，不理你啦！',899,0,'小雨')	
				do return end
			end	
		else
			say('我，我实在也拿不出这么多钱……',0,1)
			say('那我也没办法了，存好钱再来找我吧。',899,0,'小雨')
		end
	else
		do return end
	end
end
