		else
			say("你可要好生考虑。 ",448, 0,"风骚骚");
		end	
	elseif MPPD(0) == 26 and MPDJ(0) >= 1 then	  
	    if instruct_18(157) then  --判断是否有鸳鸯刀
		    say("能拿到《鸳鸯刀》说明你的实力已然大增，来看看本门的进阶武学吧。",448, 0,"风骚骚");
	        if MPDJ(0) < 2 then
			    JoinMP(0,26,2)
