    do return end
end

newkdef[1400]=function()
	local x1 = CC.ScreenW/2 - 180 ;
	local y1 = 50;
	if JY.Person[512]["无用"] > 0 then
		say('白嫖剩余'..JY.Person[512]["无用"]..'次。',0,1)
		local menu = {}
		for i=1,CC.TeamNum do
			if JY.Base['队伍'..i] > 0 then 
				if JY.Person[JY.Base['队伍'..i]]['性别'] ~= 0 then
					menu[#menu+1] = {JY.Person[JY.Base['队伍'..i]]['姓名'],nil,1,JY.Base['队伍'..i]}
				end
			end
		end
		if #menu <= 0 then 
			say('没有白嫖之人，我来此又有何意义…….',0,1)
			return
		end
		menu[#menu+1] = {'退出',nil,1}
		DrawStrBox(CC.MainMenuX,CC.MainSubMenuY,"请选择白嫖对象",LimeGreen,CC.DefaultFont,C_GOLD);
		local r = ShowMenu(menu,#menu,0,CC.MainMenuX,CC.MainSubMenuY+50,0,0,1,1,CC.DefaultFont,C_ORANGE,C_WHITE);
		if r == #menu then 
			return
		end
		if r > 0 then 
			local menu3 = {}
			local id = menu[r][4]
			local len = 0
			for i = 1,#menu do 
				if len < #menu[r][1] then 
					len = #menu[r][1] 
				end
			end
			if CC.PTF[id] ~= nil then
				for i,v in pairs(CC.PTF[id]) do 
					if TFJS[i] ~= nil then
						if CC.PTF[0][i] == nil then
							menu3[#menu3+1] = {string.sub(TFJS[i][1],3),nil,1,i}
						end
					elseif CC.PTFSM[i] ~= nil then
						if CC.PTF[0][i] == nil then
							menu3[#menu3+1] = {CC.PTFSM[i][1],nil,1,i}
						end
					end
				end
			end
			if #menu3 > 0 then 
				local r2 = ShowMenu(menu3,#menu3,0,CC.MainMenuX+CC.DefaultFont*len/2+15,CC.MainSubMenuY+50,0,0,1,1,CC.DefaultFont,C_ORANGE,C_WHITE);
				if r2 > 0 then 
					CC.PTF[0][menu3[r2][4]] = 1
					say('让我们愉快的双修吧…….',0,1)
					dark()
					light()
					JY.Person[512]["无用"] = JY.Person[512]["无用"] - 1
				end
			end
		end
	end
end

